export class Equipo {
  constructor(id, nombre, ganados, empatados, perdidos, jugadores) {
    this._id = id, this._nombre = nombre, this._ganados = ganados, this._empatados = empatados, this._perdidos = perdidos, this._jugadores = jugadores;
  }

  get id() {
    return this._id;
  }

  get nombre() {
    return this._nombre;
  }

  get ganados() {
    return this._ganados;
  }

  get empatados() {
    return this._empatados;
  }

  get perdidos() {
    return this._perdidos;
  }

  get jugadores() {
    return this._jugadores;
  }

  set nombre(nombre) {
    this.nombre = nombre;
  }

  set ganados(ganados) {
    this.ganados = ganados;
  }

  set empatados(empatados) {
    this.empatados = empatados;
  }

  set perdidos(perdidos) {
    this.perdidos = perdidos;
  }

  set jugadores(jugadores) {
    this.jugadores = jugadores;
  }

  get puntos() {
    let res = this._ganados * 3 + this._empatados;
    return res;
  }

  get golesF() {
    let res = 0;

    for (let j of this.jugadores) {
      res = res + j.goles;
    }

    return res;
  }

  get golesC() {
    let res = 0;

    for (let j of this.jugadores) {
      res = res + j.golesEncajados;
    }

    return res;
  }

  get df() {
    let res = 0;
    res = this.golesF - this.golesC;
    return res;
  }

  get amarillas() {
    let res = 0;

    for (let j of this.jugadores) {
      res = res + j.tarjetasAmarillas;
    }

    return res;
  }

  get rojas() {
    let res = 0;

    for (let j of this.jugadores) {
      res = res + j.tarjetasRojas;
    }

    return res;
  }

}
